// Yardımcı Fonksiyon: Şehir adını girince tam listeyi oluşturur
export const getImsakiye = (city: string): ImsakiyeDay[] => {
    const times = CITY_TIMES[city];
    if (!times) return [];

    return RAMADAN_DAYS.map((day, index) => {
        // Güvenlik kontrolü: Eğer zaman verisi gün sayısından azsa son günü tekrarla veya boş dön
        const timeData = times[index] || ["00:00", "00:00"];
        return {
            hijriDay: day.h,
            gregorianDate: day.g,
            dayName: day.d,
            imsak: timeData[0],
            iftar: timeData[1],
            isKadir: day.isKadir
        };
    });
};
